mapper.update:
update Post
set status = 'N'
where postIdx = #{postIdx};

mapper.select:
select status from PostLike
where userIdx = #{userIdx}
  and postIdx = #{postIdx};

mapper.update:
update PostLike
set status = #{state}
where postIdx = #{postIdx} and userIdx = #{userIdx};

mapper.select:
select u.userIdx
from UserInfo u
where u.identification = #{identification}
